event.script:

var SubmissionDate        = xfa.resolveNode("incident.Page1.reportDate").rawValue;
var IncidentDate          = xfa.resolveNode("incident.Page1.adverseEventDateFrom").rawValue;
var IncidentDateTo        = xfa.resolveNode("incident.Page1.adverseEventDateTo").rawValue;
var AwarnessDate          = xfa.resolveNode("incident.Page1.mfrAwarenessDate").rawValue;
var AwarReportabilityDate = xfa.resolveNode("incident.Page1.mfrAwarenessReportabilityDate").rawValue;

if (SubmissionDate){
	if (SubmissionDate < util.printd("yyyy-mm-dd",new Date())){
		app.alert("The expected date of submission cannot be lower than the today date.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(AwarnessDate){
	if (SubmissionDate < AwarnessDate){
		app.alert("The date of submission cannot be lower than the manufacturer awarness date.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(IncidentDate){
	if (SubmissionDate < IncidentDate){
		app.alert("The date of submission cannot be lower than the date of incident.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(IncidentDateTo){
	if (SubmissionDate < IncidentDateTo){
		app.alert("The date of submission cannot be lower than the date of incident.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(AwarReportabilityDate){
	if (SubmissionDate < AwarReportabilityDate){
		app.alert("The date of submission cannot be lower than the manufacturer awarness date of reportability.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

var SubmissionDate        = xfa.resolveNode("incident.Page1.reportDate").rawValue;
var IncidentDate          = xfa.resolveNode("incident.Page1.adverseEventDateFrom").rawValue;
var IncidentDateTo        = xfa.resolveNode("incident.Page1.adverseEventDateTo").rawValue;
var AwarnessDate          = xfa.resolveNode("incident.Page1.mfrAwarenessDate").rawValue;
var AwarReportabilityDate = xfa.resolveNode("incident.Page1.mfrAwarenessReportabilityDate").rawValue;

if(SubmissionDate){
	if (IncidentDate > SubmissionDate){
		app.alert("The date of incident cannot be greater than the date of submission.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(AwarReportabilityDate){
	if (IncidentDate > AwarReportabilityDate){
		app.alert("The date of incident cannot be greater than the manufacturer awarness of reportability date.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(AwarnessDate){
	if (IncidentDate > AwarnessDate){
		app.alert("The date of incident cannot be greater than the manufacturer awareness date.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

adverseEventDateTo.rawValue = this.rawValue

if(reportDate.rawValue){
	if (this.rawValue > reportDate.rawValue){
		app.alert("The date of incident cannot be greater than the date of submission."); 
		this.rawValue = "";
	}
}

if(mfrAwarenessDate.rawValue){
	if (this.rawValue > mfrAwarenessDate.rawValue){
		app.alert("The date of incident cannot be greater than the manufacturer awareness date."); 
		this.rawValue = "";
	}
}

if(adverseEventDateFrom.rawValue){
	if (this.rawValue < adverseEventDateFrom.rawValue){
		app.alert("The date inserted is not correct."); 
		this.rawValue = "";
	}
}

var SubmissionDate        = xfa.resolveNode("incident.Page1.reportDate").rawValue;
var IncidentDate          = xfa.resolveNode("incident.Page1.adverseEventDateFrom").rawValue;
var IncidentDateTo        = xfa.resolveNode("incident.Page1.adverseEventDateTo").rawValue;
var AwarnessDate          = xfa.resolveNode("incident.Page1.mfrAwarenessDate").rawValue;
var AwarReportabilityDate = xfa.resolveNode("incident.Page1.mfrAwarenessReportabilityDate").rawValue;

if(SubmissionDate){
	if (AwarnessDate > SubmissionDate){
		app.alert("The manufacturer awareness date cannot be greater than the date of submission.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(IncidentDateTo){
	if (AwarnessDate < IncidentDateTo){
		app.alert("The manufacturer awareness date cannot be lower than the date of incident.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(IncidentDate){
	if (AwarnessDate < IncidentDate){
		app.alert("The manufacturer awareness date cannot be lower than the date of incident.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(AwarReportabilityDate){
	if (AwarnessDate > AwarReportabilityDate){
		app.alert("The manufacturer awareness date cannot be greater than the date of manufacturer awareness of reportability.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

var SubmissionDate        = xfa.resolveNode("incident.Page1.reportDate").rawValue;
var IncidentDate          = xfa.resolveNode("incident.Page1.adverseEventDateFrom").rawValue;
var IncidentDateTo        = xfa.resolveNode("incident.Page1.adverseEventDateTo").rawValue;
var AwarnessDate          = xfa.resolveNode("incident.Page1.mfrAwarenessDate").rawValue;
var AwarReportabilityDate = xfa.resolveNode("incident.Page1.mfrAwarenessReportabilityDate").rawValue;

if(SubmissionDate){
	if (AwarReportabilityDate > SubmissionDate){
		app.alert("The manufacturer awareness of reportability date cannot be greater than the date of submission.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(IncidentDateTo){
	if (AwarReportabilityDate < IncidentDateTo){
		app.alert("The manufacturer awareness of reportability date cannot be lower than the date of incident.",1,0,"Warning"); 
		this.rawValue = "";
	}
}

if(AwarnessDate){
	if (AwarReportabilityDate < AwarnessDate){
		app.alert("The manufacturer awareness of reportability date cannot be lower than the manufacturer awareness date.",1,0,"Warning"); 
		this.rawValue = "";
	}
}	

if(IncidentDate){
	if (AwarReportabilityDate < IncidentDate){
		app.alert("The manufacturer awareness of reportability date cannot be lower than the date of incident.",1,0,"Warning"); 
		this.rawValue = "";
	}
}


this.rawValue = glob.trim(this.rawValue);

if (this.rawValue){
	if (this.rawValue < util.printd("yyyy-mm-dd",new Date())){
		app.alert("The expected date of the next report cannot be lower than the today date."); 
		this.rawValue = "";
	}
}

