calculate.script:
var EudamedID        = xfa.resolveNode("incident.Page2.pmcfpmpfId");
var LblPleaseProvide = xfa.resolveNode("incident.Page2.P[13].T53lbl-PleaseProvide");	
var radioboutton     = xfa.resolveNode("incident.Page2.P[13].PMCF-Question");

if (this.rawValue == "Yes"){
	EudamedID.presence          = "visible";	
	EudamedID.validate.nullTest = "error";
	LblPleaseProvide.presence   = "visible";
	radioboutton.rawValue       = "Yes";
}else if (this.rawValue == "No"){
	EudamedID.presence          = "invisible";	
	EudamedID.validate.nullTest = "";
	EudamedID.rawValue          = "";
	LblPleaseProvide.presence   = "invisible";	
	radioboutton.rawValue       = "No";
} else{
	EudamedID.presence          = "invisible";	
	EudamedID.validate.nullTest = "";
	EudamedID.rawValue          = "";
	LblPleaseProvide.presence   = "invisible";	
	radioboutton.rawValue       = "";
}

