validate.script:

if ( !this.isNull) {
	var NBid    = this.rawValue;
	var message = "";

	if( !Validation.isNumber(NBid)){
		message =  "The value for this field must be a number. It is not the case.\n";
		message += "Please modify";
		app.alert(message,1,0,"Warning");
		this.rawValue = "";	
	}
	else{
		this.isNull || (this.rawValue >= -Math.pow(2,63) && this.rawValue <= (Math.pow(2,63)-1));
	}	
}

