	glob.riskAssReviewedVal = RiskReviewedVal;
}else if (glob.riskAssReviewedVal == RiskReviewedVal){
	glob.riskAssReviewedVal = "";
	RiskReviewedVal = "";
	this.rawValue = "";
	RationalNoReview.rawValue          = null;
	RationalNoReview.validate.nullTest = "";
	RationalNoReview.access            = "protected";
	RiskAdequate.rawValue              = "Off";
	RiskAdequate.access                = "protected";
}else{
	glob.riskAssReviewedVal = RiskReviewedVal;
}

switch(RiskReviewedVal){
	case "Yes":
		RationalNoReview.rawValue          = "";
		RationalNoReview.validate.nullTest = "";
		RationalNoReview.access            = "protected";

		RiskAdequate.access                = "open";
		RiskAdequate.validate.nullTest     = "error";
		glob.riskAssAdequateVal            = "";
		break;
	case "No":
		RationalNoReview.access            = "open";
		RationalNoReview.validate.nullTest = "error";
		xfa.host.setFocus(RationalNoReview);	

		RiskAdequate.rawValue          = "";	
		RiskAdequate.validate.nullTest = "";
		RiskAdequate.access            = "protected";
		glob.riskAssAdequateVal        = "";

		RiskResults.rawValue           = "";
		RiskResults.validate.nullTest  = "";
		RiskResults.access             = "protected";	
		break;

	default:

		RationalNoReview.rawValue          = "";
		RationalNoReview.validate.nullTest = "";
		RationalNoReview.access            = "protected";

		RiskAdequate.rawValue          = "";	
		RiskAdequate.validate.nullTest = "";
		RiskAdequate.access            = "protected";
		glob.riskAssAdequateVal        = "";

		RiskResults.rawValue           = "";
		RiskResults.validate.nullTest  = "";
		RiskResults.access             = "protected";	
}

