	// app.alert("nReq: " + nReq);

	if (nReq > 0) // incident_subform is the root object of this form to which all fields belong (are parented)
	{
		ScriptObject.bFieldsHighlighted = true;
		if (nReq<21){
			// app.alert(glob.sMandatory);
			app.alert(myTextAlert + nReq + " mandatory fields weren't filled. Please provide the requested information prior to submitting this form.\n\n" + glob.sMandatory, 0, 0, "Mandatory Fields Check");
		}else{		
			sFirst20 = "";
			for (var i = 0; i < 20; i++) {
				sFirst20 = sFirst20 + glob.sMandatory.split('\n')[i] + '\n';
			}
			app.alert(myTextAlert + nReq + " mandatory fields weren't filled. Please provide the requested information prior to submitting this form. The first 20 are:\n\n" + sFirst20 , 0, 0, "Mandatory Fields Check");
		}
		valid = false;
	}

	if(valid == true){
		app.alert("All mandatory fields have been filled-in", 0, 0, "Mandatory Fields Check");
	}

