// This function shows the hidden long text fields (page13)

	function showLongTextFields(){
		for (i=1; i<25; i++){
	    if(!xfa.resolveNode("incident.#subform[12].TextField" + i).isNull){
	      xfa.resolveNode("incident.#subform[12].TextField" + i).presence="visible"; 
	    }else{
	      xfa.resolveNode("incident.#subform[12].TextField" + i).presence="hidden"; 
	    }
	  }
	} 

