	function showResolve(s) {
		try {
			app.alert(s + " " + xfa.resolveNode(s) + " " + xfa.resolveNode(s).rawValue);
		} catch(oErr) {
			showError(this, "showResolve '" + s + "' error " + oErr.description);
		}
	}

	function checkRiskAssRev(){
		try {
		 	if (xfa.resolveNode("incident.Page8.#subform[0].Section42d.riskAssReviewed").rawValue == "No") {
				xfa.resolveNode("incident.Page8.#subform[0].#subform[13].rationaleNoReview").access = "open";
		 		xfa.resolveNode("incident.Page8.#subform[0].#subform[13].rationaleNoReview").validate.nullTest = "error";
				xfa.resolveNode("incident.Page8.#subform[0].MIR42d2.riskAssAdequate").access = "protected";	
				xfa.resolveNode("incident.Page8.#subform[0].MIR42d2.riskAssAdequate").validate.nullTest = "";
				xfa.resolveNode("incident.Page8.#subform[0].#subform[15].riskAssResults").validate.nullTest = "";	
			} else if (xfa.resolveNode("incident.Page8.#subform[0].Section42d.riskAssReviewed").rawValue == "Yes") {
		 		xfa.resolveNode("incident.Page8.#subform[0].#subform[13].rationaleNoReview").validate.nullTest = "";
				xfa.resolveNode("incident.Page8.#subform[0].#subform[13].rationaleNoReview").access = "protected";
				xfa.resolveNode("incident.Page8.#subform[0].MIR42d2.riskAssAdequate").access = "open";
				xfa.resolveNode("incident.Page8.#subform[0].MIR42d2.riskAssAdequate").validate.nullTest = "error";
				xfa.resolveNode("incident.Page8.#subform[0].#subform[15].riskAssResults").validate.nullTest = "error";
			}
		} catch(oErr) {
			showError(this, "checkRiskAssRev error " + oErr.description);
		}
	}

