	function checkRiskClass(){	

		var DocumentType    = xfa.resolveNode("incident.Page1.reportType").rawValue; 
		var ClasMDD         = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDD").rawValue;
		var ClassIVDD       = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDD").rawValue;
		var ClassMDR        = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDR").rawValue;
		var ClassIVDR       = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDR").rawValue;

		var MDR_SterileCond = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType3").rawValue;
		var MDR_Measuring   = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType4").rawValue;
		var MDR_ReusableIns = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType5").rawValue;

		var IVDR_SterileCond= xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType7").rawValue;

		var Fld_NBID        = xfa.resolveNode("incident.Page4.nbIdNum");			// Section 2.3.p 
		var Fld_NBCertifNbr = xfa.resolveNode("incident.Page4.nbCertNum");			// Section 2.3.p 

		try {

			if ((DocumentType == "Combined initial and final") || (DocumentType == "Final (Reportable incident)") || (DocumentType == "Final (Non-reportable incident)")){

				// ----------------------------------------------------------------------------------------------------------------------------
				// Following the report type : "Combined initial and final", "Final (Reportable incident)" or "Final (Non-reportable incident)"
				// And following the option selected into Class risk (2.4) 
				// Change the validation of the two fields : "Eudamed ID" and "UDI production identifier" (2.1.a)
				// ----------------------------------------------------------------------------------------------------------------------------

				if ((ClasMDD == "active implant") || (ClasMDD == "class III") || (ClasMDD == "class IIb") || (ClasMDD == "class IIa")){
					Fld_NBID.validate.nullTest        = "error";
					Fld_NBCertifNbr.validate.nullTest = "error";
				}
				else if ((ClasMDD == "class Is") || (ClasMDD == "class Im") || (ClasMDD == "class Ism")){
					switch (ClassMDD){
						case "class Is":
							Fld_NBID.validate.nullTest        = "error";
							Fld_NBCertifNbr.validate.nullTest = "error";
							break;
						default:
							Fld_NBID.validate.nullTest        = "";
							Fld_NBCertifNbr.validate.nullTest = "";
					}
				}		


			}






		// --------------------------------------------
		// If an option is selected in the MDR (2.4.d)
		// give access to the type of MDR (check boxes)
		// --------------------------------------------


		if (xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDR").rawValue == ""){
		}else{
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType1").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType2").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType3").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType4").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType5").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType6").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType7").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType9").access  = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType10").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassMDRType11").access = "open";
		}

		// --------------------------------------------
		// If an option is selected in the IVDR (2.4.d)
		// give access to the type of IVDR (check boxes)
		// --------------------------------------------

		if (xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDR").rawValue == ""){
		}else{
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType1").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType2").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType3").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType4").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType5").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType6").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType7").access = "open";
			xfa.resolveNode("incident.Page5.#subform[0].#subform[1].deviceClassIVDRType8").access = "open";
		}
		} catch(oErr) {
			showError(this, "checkRiskClass error " + oErr.description);
		}
	}

	function populateSubmitter(){
		try {

			var ReporterStatus = Page1.statusReporter.rawValue;

			if ( ReporterStatus == "Authorised representative" || ReporterStatus == "Manufacturer" ){

				// Remove the attribute 'Mandatory' into bloc 1.3.2 Manufacturer information

				Page2.mfrName.validate.nullTest                    = "";
				Page2.mfrContactPersonFirstName.validate.nullTest  = "";
				Page2.mfrContactPersonSecondName.validate.nullTest = "";
				Page2.mfrEmailAddress.validate.nullTest            = "";
				Page2.mfrPhone.validate.nullTest                   = "";
				Page2.mfrCountry.validate.nullTest                 = "";
				Page2.mfrOrgStreet.validate.nullTest               = "";
				Page2.mfrOrgStreetNum.validate.nullTest            = "";
				Page2.mfrAddress.validate.nullTest                 = "";
				Page2.mfrOrgPOBox.validate.nullTest                = "";
				Page2.mfrCity.validate.nullTest                    = "";
				Page2.mfrPostcode.validate.nullTest                = "";

				// Remove the attribute 'Mandatory' into bloc 1.3.3 Authorised representative information
				Page2.ARName.validate.nullTest                    = "";
				Page2.ARContactPersonFirstName.validate.nullTest  = "";
				Page2.ARContactPersonSecondName.validate.nullTest = "";
				Page2.AREmailAddress.validate.nullTest            = "";
				Page2.ARPhone.validate.nullTest                   = "";
				Page2.ARCountry.validate.nullTest                 = "";

				Page3.ARStreet.validate.nullTest                  = "";
				Page3.ARStreetNum.validate.nullTest               = "";
				Page3.ARAddress.validate.nullTest                 = "";
				Page3.ARPOBox.validate.nullTest                   = "";
				Page3.ARCity.validate.nullTest                    = "";
				Page3.ARPostCode.validate.nullTest                = "";
				Page3.Rectangle1.presence                         = "invisible";		
			}		
		} catch(oErr) {
			showError(this, "populateSubmitter error " + oErr.description);
		}
	}

	function checkSubmitter(){

		// Selection of the 'Submitter of the report'
		// Value: 'Manufacturer', 'Authorised reprensentative' and 'Others'
		// ----------------------------------------------------------------

		var Submitter      = xfa.resolveNode("incident.Page1.statusReporter").rawValue;
		var OtherSubmitter = xfa.resolveNode("incident.Page1.reporterOtherText"); 

		var MandatoryVal   = "error";
		var FreeVal        = "";

		var RVisible       = "visible";		// Status to display or hide the rectange
		var NVisible       = "invisible";

		// Default value to be applied when opening the PDF

		var Grp1           = MandatoryVal;	// statut to be applied for the section 1.3.2
		var Grp2           = FreeVal;		// statut to be applied for the section 1.3.3
		var Grp3           = FreeVal;		// statut to be applied for the section 1.3.4

		var Rct1           = NVisible;
		var Rct2           = NVisible;
		var Rct3           = NVisible;

		// RD - 2024-12-11 - START
		// Added the initialisation of the variables controlling the read-only behaviour of the 1.3.x sections.
		var AccessOpen     = "open";
		var AccessRO       = "readOnly";

		// Default value to be applied when opening the PDF.
		var Acc1           = AccessOpen;    // Data can be filled in section 1.3.2
		var Acc2           = AccessOpen;    // Data can be filled in section 1.3.3
		var Acc3           = AccessRO;      // Fields locked in section 1.3.4
		// RD - 2024-12-11 - END

		// RD - 2024-12-17 - START
		// Added initialisation of selected country value to guarantee the appropriate behaviour of the
		// various sections.
		ManufacturerCountry = xfa.resolveNode("incident.Page2.mfrCountry").rawValue;
		var CountryWorld    = glob.checkCountry(ManufacturerCountry);
		var CountryEU       = glob.checkCountryEEA(ManufacturerCountry);
		var CountryEUCHTR   = glob.checkCountryEEACHTR(ManufacturerCountry);
		// RD - 2024-12-17 - END

		// Declaration

		// Fields on page 2
		// Section 1.3.2
		// -----------------------------------------------------------------------------------------
		var S132_ManufacturerName    = xfa.resolveNode("incident.Page2.mfrName");
		var S132_RegistrationNumber  = xfa.resolveNode("incident.Page2.mfrSRN");
		var S132_FirstName           = xfa.resolveNode("incident.Page2.mfrContactPersonFirstName");
		var S132_LastName            = xfa.resolveNode("incident.Page2.mfrContactPersonSecondName");
		var S132_Email               = xfa.resolveNode("incident.Page2.mfrEmailAddress");
		var S132_Phone               = xfa.resolveNode("incident.Page2.mfrPhone");
		var S132_Country             = xfa.resolveNode("incident.Page2.mfrCountry");
		var S132_Street              = xfa.resolveNode("incident.Page2.mfrOrgStreet");
		var S132_City                = xfa.resolveNode("incident.Page2.mfrCity");
		var S132_Postalcode          = xfa.resolveNode("incident.Page2.mfrPostcode");

		// Fields on page 2
		// Section 1.3.3
		// -----------------------------------------------------------------------------------------
		var S133_ManufacturerName    = xfa.resolveNode("incident.Page2.ARName");
		var S133_RegistrationNumber  = xfa.resolveNode("incident.Page2.ARSRN");
		var S133_FirstName           = xfa.resolveNode("incident.Page2.ARContactPersonFirstName");
		var S133_LastName            = xfa.resolveNode("incident.Page2.ARContactPersonSecondName");
		var S133_Email               = xfa.resolveNode("incident.Page2.AREmailAddress");
		var S133_Phone               = xfa.resolveNode("incident.Page2.ARPhone");
		var S133_Country             = xfa.resolveNode("incident.Page2.ARCountry");

		// Fields on page 3
		// Section 1.3.3
		// -----------------------------------------------------------------------------------------
		var S133_Street              = xfa.resolveNode("incident.Page3.ARStreet");
		var S133_City                = xfa.resolveNode("incident.Page3.ARCity");
		var S133_Postalcode          = xfa.resolveNode("incident.Page3.ARPostCode");	

		// Fields on page 3
		// Section 1.3.4
		// -----------------------------------------------------------------------------------------

		// Fields on page 3 
		// Section 1.3.4
		// -----------------------------------------------------------------------------------------
		var S134_ManufacturerName    = xfa.resolveNode("incident.Page3.reporterOrgName");
		var S134_FirstName           = xfa.resolveNode("incident.Page3.reporterContactPersonFirstName");
		var S134_LastName            = xfa.resolveNode("incident.Page3.reporterContactPersonSecondName");
		var S134_Email               = xfa.resolveNode("incident.Page3.reporterOrgEmailAddress");
		var S134_Phone               = xfa.resolveNode("incident.Page3.reporterOrgPhone");
		var S134_Country             = xfa.resolveNode("incident.Page3.reporterOrgCountry");
		var S134_Street              = xfa.resolveNode("incident.Page3.reporterOrgStreet");
		var S134_StreetNum           = xfa.resolveNode("incident.Page3.reporterOrgStreetNum");
		var S134_StreetComplement    = xfa.resolveNode("incident.Page3.reporterOrgAddress");
		var S134_POBox               = xfa.resolveNode("incident.Page3.reporterOrgPOBox");
		var S134_City                = xfa.resolveNode("incident.Page3.reporterOrgCity");
		var S134_Postalcode          = xfa.resolveNode("incident.Page3.reporterOrgPostcode");				

		try {
			// RD - 2024-12-11 - START
			// Added configuration of the read-only status ("Accx").
			// RD - 2024-12-17
			// Added checks to standardise the "required" behaviour of section 1.3.3 based on
			// the selected "Manufacturer" country.

			// Configure variables following the selection of the submitter
			switch (Submitter){
				case "Manufacturer":
					Grp1 = MandatoryVal;
					// Grp2 = FreeVal;
					if ((CountryEU) || (ManufacturerCountry == 'TR')){
						Grp2 = FreeVal;					
					}
					else {
						Grp2 = MandatoryVal;					
					}
					Grp3 = FreeVal;
					Rct1 = NVisible;
					Rct2 = NVisible;
					Rct3 = NVisible;
					Acc1 = AccessOpen;
					Acc2 = AccessOpen;
					Acc3 = AccessRO;
					break;
				case "Authorised representative":
					Grp1 = MandatoryVal;
					//Grp2 = MandatoryVal;
					if ((CountryEU) || (ManufacturerCountry == 'TR')){
						Grp2 = FreeVal;					
					}
					else {
						Grp2 = MandatoryVal;					
					}
					Grp3 = FreeVal;
					Rct1 = NVisible;
					Rct2 = NVisible;
					Rct3 = NVisible;
					Acc1 = AccessOpen;
					Acc2 = AccessOpen;
					Acc3 = AccessRO;
					break;
				case "Others":
					Grp1 = MandatoryVal;
					//Grp2 = MandatoryVal;
					if ((CountryEU) || (ManufacturerCountry == 'TR')){
						Grp2 = FreeVal;					
					}
					else {
						Grp2 = MandatoryVal;					
					}
					Grp3 = MandatoryVal;
					Rct1 = NVisible;
					Rct2 = NVisible;
					Rct3 = NVisible;
					Acc1 = AccessOpen;
					Acc2 = AccessOpen;
					Acc3 = AccessOpen;
					break;
				default:
					Grp1 = MandatoryVal;
					Grp2 = FreeVal;
					Grp3 = FreeVal;
					Rct1 = NVisible;
					Rct2 = NVisible;
					Rct3 = NVisible;
					Acc1 = AccessOpen;
					Acc2 = AccessOpen;
					Acc3 = AccessRO;
			}
			// RD - 2024-12-11 - END

			// Change the access (mandatory or not) of the fields following the selection of the Submitter

			// Section 1.3.2
			S132_ManufacturerName.validate.nullTest    = Grp1;
			S132_RegistrationNumber.validate.nullTest  = Grp1;
			S132_FirstName.validate.nullTest           = Grp1;
			S132_LastName.validate.nullTest            = Grp1;
			S132_Email.validate.nullTest               = Grp1;
			S132_Phone.validate.nullTest               = Grp1;
			S132_Country.validate.nullTest             = Grp1;
			// S132_Street.validate.nullTest              = Grp1;
			S132_City.validate.nullTest                = Grp1;
			S132_Postalcode.validate.nullTest          = Grp1;

			Page2.Rectangle1.presence                  = Rct1;

			// Section 1.3.3
			S133_ManufacturerName.validate.nullTest    = Grp2;
			S133_RegistrationNumber.validate.nullTest  = Grp2;
			S133_FirstName.validate.nullTest           = Grp2;
			S133_LastName.validate.nullTest            = Grp2;
			S133_Email.validate.nullTest               = Grp2;
			S133_Phone.validate.nullTest               = Grp2;
			S133_Country.validate.nullTest             = Grp2;
			S133_Street.validate.nullTest              = Grp2;
			S133_City.validate.nullTest                = Grp2;
			S133_Postalcode.validate.nullTest          = Grp2;

			Page3.Rectangle1.presence                  = Rct2;

			// Section 1.3.4
			S134_ManufacturerName.validate.nullTest    = Grp3;
			S134_FirstName.validate.nullTest           = Grp3;
			S134_LastName.validate.nullTest            = Grp3;
			S134_Email.validate.nullTest               = Grp3;
			S134_Phone.validate.nullTest               = Grp3;
			S134_Country.validate.nullTest             = Grp3;
			S134_Street.validate.nullTest              = Grp3;
			S134_City.validate.nullTest                = Grp3;
			S134_Postalcode.validate.nullTest          = Grp3;	

			Page3.Rectangle2.presence                  = Rct3;

			// RD - 2024-12-11 - START
			// Added code actually updating the read-only status of section 1.3.4 based on the evaluation
			// performed earlier in the "checkSubmitter" function.

			// Fields accessible or not
			S134_ManufacturerName.access               = Acc3;
			S134_FirstName.access                      = Acc3;
			S134_LastName.access                       = Acc3;
			S134_Email.access                          = Acc3;
			S134_Phone.access                          = Acc3;
			S134_Country.access                        = Acc3;
			S134_Street.access                         = Acc3;
			S134_StreetNum.access                      = Acc3;
			S134_StreetComplement.access               = Acc3;
			S134_POBox.access                          = Acc3;
			S134_City.access                           = Acc3;
			S134_Postalcode.access                     = Acc3;
			// RD - 2024-12-11 - END

			if(Submitter == "Others"){
				OtherSubmitter.access            = "open";
				OtherSubmitter.validate.nullTest = MandatoryVal;
				xfa.host.setFocus(OtherSubmitter);
			}else{
				OtherSubmitter.rawValue          = null;
				OtherSubmitter.access            = "protected";
				OtherSubmitter.validate.nullTest = FreeVal;			
			}



		} catch(oErr) {
			showError(this, "checkSubmitter error: " + oErr.description);
		}	
	}

