				xfa.resolveNode("incident.Page10.#subform[0].#subform[1].Rectangle1").presence                        = "invisible";
				xfa.resolveNode("incident.Page10.#subform[0].#subform[15].Rectangle2").presence                       = "invisible";
				break;

			default:
				// Page 01 
				Fld_ncaName.validate.nullTest             = "error";
				DocumentType.validate.nullTest            = "error";
				Fld_reportNextDate.validate.nullTest      = "error";
				Fld_eventClassification.validate.nullTest = "error";
				Opt_statusReporter.validate.nullTest      = "error";
				MfrReferenceNumber.validate.nullTest      = "error";

				// Page 04

				Fld_DeviceDesc.validate.nullTest        = "";

				// Page 05

				// START AL (SB-10371): 09-04-2025 - 16:44
				Fld_DeviceF_Fill.validate.nullTest      = "";	// Remove the mandatory attribute at opening.
				// END 

				/*if (CompetentAuthName.presence == "visible"){
					CompetentAuthName.validate.nullTest = "";
				}


				Fld_LegUnknown.validate.nullTest        = "";
				Fld_DeviceClass.validate.nullTest       = "";
				ClasMDD.validate.nullTest               = "";
				ClassIVDD.validate.nullTest             = "";
				ClassMDR.validate.nullTest              = "";
				ClassIVDR.validate.nullTest             = "";
				*/

				// Page 10

				IMDRF_AnnexA.validate.nullTest           = "";
				IMDRF_AnnexC.validate.nullTest           = "";


				// Shapes on page 05 and page 10
				xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Rectangle_Rouge").presence                    = "visible";
				xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section_25a-Countries.CountryShape").presence = "invisible";
				xfa.resolveNode("incident.Page10.#subform[0].#subform[1].Rectangle1").presence                        = "invisible";
				xfa.resolveNode("incident.Page10.#subform[0].#subform[15].Rectangle2").presence                       = "invisible";			
		}


		checkRiskClass();

		} catch(oErr) {
			showError(this, "checkTypeReport error: " + oErr.description);
		}
	}

	function trim(s) {
	  if (s != null){
	  	return s.replace (/^\s+/, '').replace (/\s+$/, '');
	  }else{
	    return s;
	  }
	}

	function showError(oField, s) {
	  showDialog(oField, s, 0, "ERROR");
	}

	function showWarning(oField, s) {
	  showDialog(oField, s, 1, "WARNING");
	}

	function showInfo(oField, s) {
	  showDialog(oField, s, 3, "INFO");
	}

	function showDialog(oField, s, iType, sType) {
		try {
	  		app.alert(s, iType, 0, sType + " in " + this.name + " [" + oField.name + "::" + xfa.event.name + "] " + xfa.resolveNode(oField.somExpression + ".caption.value.#text").value);
	  	}
	  	catch (oErr_2) {
	  		app.alert(s, iType, 0, sType + " in " + this.name + " [" + oField.name + "::" + xfa.event.name + "]");  	
	  	}
	}

	function makeAllFieldsReadOnly(oNode)
	{

		var s;
		// app.alert(oNode.className);
		if (oNode.className == "exclGroup" || oNode.className == "subform"  || oNode.className == "subformSet" || oNode.className ==  "area") {
																		 		// Look for child fields.
			for (var i = 0; i < oNode.nodes.length; i++) {
				var oChildNode = oNode.nodes.item(i);
				makeAllFieldsReadOnly(oChildNode);
			}
		}
		else if (oNode.className == "field") {

			// app.alert("field: " + oNode.className + " " + oNode.ui.oneOfChild.className + " " + oNode.access);
			switch (oNode.ui.oneOfChild.className) { 
				case "button":
					s = oNode.caption.value.resolveNode("#text").value;
					if (s != "print" && s != "Drucken") {
						oNode.access = "readOnly";
					}
					break;
				case "checkButton":
				case "choiceList":
				case "dateTimeEdit":
				case "imageEdit":
				case "numericEdit":
				case "passwordEdit":
				case "textEdit":
				case "signature":
				case "barcode":
					oNode.access = "readOnly";
					break;
				default:
					app.alert("unknown field type");
					break;
			}
		}
		return 0;
	}   

