	function checkCountry(sCountryCode)
	{

		var findCountry = 0;

		if (sCountryCode != null){
			if ((sCountryCode.length != 2) || (sCountryCode.toUpperCase() != sCountryCode)) {
				showError(this, "'" + sCountryCode + "' is invalid. Use 2 characters in upper case.");
				return false;
			}
			else {
				findCountry = sIsoCountries.indexOf("|" + sCountryCode + "|");

				if (findCountry < 0) {
					return false;
				}else{
					return true;
				}	
			}
		}else{
			this.rawValue = "";
		}
	}

	function checkCountryEEACHTR(sCountryCode)
	{
		var findCountry = 0;

		if (sCountryCode != null){
			if ((sCountryCode.length != 2) || (sCountryCode.toUpperCase() != sCountryCode)) {
				showError(this, "'" + sCountryCode + "' is invalid. Use 2 characters in upper case.");
				return false;
			}
			else {	
				findCountry = sIsoCountriesEEACHTR.indexOf("|" + sCountryCode + "|");

				if ( findCountry < 0) {
					return false;
				}else{
					return true;
				}
			}
		}else{
			this.rawValue = "";
		}
	}


	function checkCountryEEA(sCountryCode)
