		}
	}


	function checkCountries()
	{
		if ((reportType.rawValue == "Final" || reportType.rawValue == "Combined initial and final") &&
			distribution_all.rawValue == null && 
			(distributionOutsideEU.rawValue == "n/a" || distributionOutsideEU.rawValue == "" || distributionOutsideEU.rawValue == null) && 
			TR.rawValue == null && 
			HR.rawValue == null &&
			AT.rawValue == null && 
			BE.rawValue == null &&
			BG.rawValue == null && 
			CH.rawValue == null &&
			CY.rawValue == null &&
			CZ.rawValue == null &&
			DE.rawValue == null &&
			DK.rawValue == null &&
			EE.rawValue == null &&
			ES.rawValue == null &&
			FI.rawValue == null &&
			FR.rawValue == null &&
			GB.rawValue == null &&
			GR.rawValue == null &&
			HU.rawValue == null &&
			IE.rawValue == null &&
			IS.rawValue == null &&
			IT.rawValue == null &&
			LI.rawValue == null &&
			LT.rawValue == null &&
			LU.rawValue == null &&
			LV.rawValue == null &&
			MT.rawValue == null &&
			NL.rawValue == null &&
			NO.rawValue == null &&
			PL.rawValue == null &&
			PT.rawValue == null &&
			RO.rawValue == null &&
			SE.rawValue == null &&
			SI.rawValue == null && 
			SK.rawValue == null) {
				app.alert("No country given where the device has been distributed. Please provide distribution info prior to submitting this form.");
	        	app.runtimeHighlight = true;
	        	xfa.event.cancelAction = 1;
		}
		return xfa.event.cancelAction;
	}

	function exportTestData(sPathFile)
	{
	//	app.alert("exportTestData: '" + sPathFile + "'");
		try {
			if (sPathFile == "") {
				var sCwd = event.target.path.substr(0, event.target.path.length - event.target.documentFileName.length);
				sPathFile = sCwd + "incident_" + iTestButton + ".xml"
			}
			xfa.host.exportData(sPathFile, 0);
		} catch(oErr) {
			showError(this, oErr.description + " in exportTestData " + oErr.fileName);
		}
	}

	function isCertified() {
		if (event.target.getField("incident[0].#subform[23].#subform[26].Signaturesfeld1[0]").signatureInfo().mdp != null) {
			return true;
		}
		else {
			return false;
		}
	}

