// a test: click all buttons on this form (except the button from which this was started)
// in: root node, button node (for exclusion)
// use in button::click
	function exerciseButtons(oNode, oButton)
	{
		if (oNode.className == "exclGroup" || oNode.className == "subform"  || oNode.className == "subformSet" || oNode.className == "area") {

							// go through all children
			for (var iNode = 0; iNode < oNode.nodes.length; iNode++) {
				var oChildNode = oNode.nodes.item(iNode);
				exerciseButtons(oChildNode, oButton);
			}
		}
		else if (oNode.className == "field") {
	        if (oNode.ui.oneOfChild.className == "button") {
	           	if (oNode.name != oButton.name) {
					oNode.execEvent("click");
				}
			}
		}
		return 0;
	}

