	//                    'N' => black
	// --------------------------------------------------------------------------------------------------
	function ChangeColorLabel(CallSection, oActive){

		var DocumentType            = "";    // xfa.resolveNode("incident.Page1.reportType").rawValue;

		/*
		DocumentType = "Initial";
		DocumentType = "Follow up";          
		DocumentType = "Combined initial and final";
		DocumentType = "Final (Reportable incident)";
		DocumentType = "Final (Non-reportable incident)";
		*/
		// Declaration for	
		// Section 2.4.f

		var DeviceFulfill          = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.DeviceFulfill");
		var DeviceFulfill0         = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.DeviceFulfill.DeviceFulfill[0]");
		var DeviceFulfill1         = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.DeviceFulfill.DeviceFulfill[1]");
		var LblDeviceFulfill       = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.LBL_DeviceFulfill");

		var CompetentAuthName      = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.CompetentAuthName");
		var RelevantName           = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.RelevantName");
		var LblRelevantName        = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.LBL_RelevantName");
		var LblCompetentAuthName   = xfa.resolveNode("incident.Page5.#subform[0].#subform[1].Section24_e_f.LBL_CompetentAuthName");

		// Section 4.1.b
		var LBL_SuspicionRelations = xfa.resolveNode("incident.Page8.#subform[0].Section41b.LBL_SuspicionRelationship");
		var RelationshipQuestion   = xfa.resolveNode("incident.Page8.#subform[0].Section41b.RelationshipQuestion");
