		var RelationShip2_4Q_N     = xfa.resolveNode("incident.Page8.#subform[0].Section42d.RelationShip2_4_f.RelationShip2_4_f_N");	// No

		var colourFont         = oActive == "Y" ? "200, 200, 200" : "0, 0, 0"  ;  // colourFont = Question ? Light gray : Black
		var mandatory          = "error";
		var free               = "";

		switch (CallSection){
			case "s2.4.a":
			case "s2.4.b":
			case "s2.4.c":
			case "s2.4.d":
