					RelationshipQuestion.validate.nullTest = free // (DocumentType == "") ? mandatory : free;
					RelationShip2_4Q.validate.nullTest     = free // (DocumentType == "") ? mandatory : free;
					DeviceFulfill.access                   = "open";
					RelationshipQuestion.access            = "open";
