				RelationshipQuestion.rawValue          = "";
				glob.RelationshipQuestionVal           = "";
				RelationShip2_4Q.rawValue              = "";
				RelationShip2_4Q.validate.nullTest     = free;
				glob.RelationshipSupicion              = "";
		}

	}

	function CheckSeriousIncident (TimePeriod, NameQt, CheckValue, Location){

		// TimePeriod: values are: 'N', 'N-1', 'N-2' and 'N-3'
		//     NameQt: values are: 'similar SI' or 'devices on market'
		// CheckValue: Value is: The value of the selected cell
		//   Location: Values are: 'Country', 'EEA' and 'World'
		// -----------------------------------------------------------

		var title      = "";
		var titleComp1 = "";
		var titleComp2 = "";
		var message    = "";
		var DisplayTxt = "";
		var error      = false;

		// Declaration of the 3 objects of the selected column

		if (NameQt == "similar SI" ){
			var lblCountry = "countryIncidentInc";
			var lblEEA     = "eeaChTrInc";
			var lblWorld   = "worldInc";		
		}else{
			var lblCountry = "countryIncidentDev";
			var lblEEA     = "eeaChTrDev";
			var lblWorld   = "worldDev";		
		}

		var txt        = "";

		// Prepare the title of the msgbox

		title  = "Period : 'Time period (" + TimePeriod + ")'\n";
		title += " Column: Number of " + NameQt + "\n";
		title += "--------------------------------------------\n";

		DisplayTxt = title;

		if (TimePeriod != null){

			// Objects declaration

			var Country = xfa.resolveNode("incident.Page11.#subform[0].#subform[1]." + lblCountry + TimePeriod);
			var EEA     = xfa.resolveNode("incident.Page11.#subform[0].#subform[1]." + lblEEA + TimePeriod);
			var World   = xfa.resolveNode("incident.Page11.#subform[0].#subform[1]." + lblWorld + TimePeriod);

			/*
			txt += "TimePeriod: " + TimePeriod + "\n";
			txt += "    Column: Number of " + NameQt + "\n";
			txt += "------------------------------------------------------------------------------------------------\n";
			txt += "Country: xfa.resolveNode('incident.Page11.#subform[0].#subform[1]." + lblCountry + TimePeriod + "')\n";
			txt += "    EEA: xfa.resolveNode('incident.Page11.#subform[0].#subform[1]." + lblEEA + TimePeriod + "')\n";
			txt += "  World: xfa.resolveNode('incident.Page11.#subform[0].#subform[1]." + lblWorld + TimePeriod + "')\n";
			txt += "------------------------------------------------------------------------------------------------\n";
			txt += "CheckValue: " + CheckValue + "\n";
			txt += "   Country: " + Country.rawValue + "\n";
			txt += "       EEA: " + EEA.rawValue + "\n";
			txt += "     World: " + World.rawValue + "\n";


			*/

			if ((CheckValue != null) && ((EEA.rawValue != null) || (World.rawValue != null))){
				switch (Location){
					case "Country":
						if (CheckValue > EEA.rawValue && (EEA.rawValue != null)){
							error = true;
							DisplayTxt += "- Note the value '" + CheckValue + "' for 'Country of serious incident' must be less than or equal to 'EEA + TR + XI'\n"; 
						}
						if (CheckValue > World.rawValue && (World.rawValue != null)){
							error = true;
							DisplayTxt += "- Note the value '" + CheckValue + "' for 'Country of serious incident' must be less than or equal to 'World'\n";
						}
						break;	
					case "EEA":
						if (CheckValue < Country.rawValue && (Country.rawValue != null)){
							error = true;
							DisplayTxt += "- Note the value '" + CheckValue + "' for 'EEA + TR + XI' must be greater than or equal to 'Country of serious incident'\n"; 
						}
						if (CheckValue > World.rawValue && (World.rawValue != null)){
							error = true;
							DisplayTxt += "- Note the value '" + CheckValue + "' for 'EEA + TR + XI' must be less than or equal to 'World'\n";
						}
						break;
					case "World":
						if (CheckValue < Country.rawValue && (Country.rawValue != null)){
							error = true;
							DisplayTxt += "- Note the value '" + CheckValue + "' for 'World' must be greater than or equal to 'Country of serious incident'\n"; 
						}
						if (CheckValue < EEA.rawValue && (EEA.rawValue != null)){
							error = true;
							DisplayTxt += "- Note the value '" + CheckValue + "' for 'World' must be greater than or equal to 'EEA + TR + XI'\n";
						}
						break;					


				}
			}		
		}

		/*
		txt += "CheckValue: " + CheckValue + "\n";
		txt += "       EEA: " + EEA.rawValue + "\n";
		txt += "     World: " + World.rawValue + "\n";
		txt += "     error: " + error + "\n";
		txt += "titleComp1: " + titleComp1 + "\n";
		txt += "titleComp2: " + titleComp2 + "\n";

		app.alert(txt);



		*/

		if (error){

			app.alert(DisplayTxt,1,0,"Warning");

			return 0;		
		}
		return 1;
	}

