	function validateCode(code){

		if (code != null){
			if (code.length <3){
				return null;
			}else if (code.length>7){
				return code.substr(0,7);
			}else{
				return code;
			}
		} else {
			return null;
		}
	}

	function validateCode8(code){

		if (code != null){
			if (code.length <3){
				return null;
			}else if (code.length>8){
				return code.substr(0,8);
			}else{
				return code;
			}
		} else {
			return null;
		}
	}

	function validateCode357(code){
		if (code != null){
			if (code.length == 3 || code.length == 5 || code.length == 7){
				return code;
			}else{
				return null;
			}
		} else {
			return null;
		}
	}

	function validateCode35(code){
		if (code != null){
			if (code.length == 3 || code.length == 5){
				return code;
			}else{
				return null;
			}
		} else {
			return null;
		}
	}

	function validateCode3(code){
		if (code != null){
			if (code.length == 3){
				return code;
			}else{
				return null;
			}
		} else {
			return null;
		}
	}

	function inRange(lValue, lLower, lUpper)
	{
		if (lValue >= lLower && lValue <= lUpper) {
			return true;
		}
		else {
			xfa.host.messageBox(lValue + " out of range (" + lLower + "-" + lUpper + ")");
			return false;
		}
	}

	function dualRange(lValue, bMale, lLower1, lUpper1, lLower2, lUpper2)
	{
		if (bMale) {
			return inRange(lValue, lLower1, lUpper1);
		}
		else {
			return inRange(lValue, lLower2, lUpper2);
		}	
	}

	// validateAll
	// 
	function validateAll() 
	{
		for (var nPage = 0; nPage < xfa.host.numPages; nPage++) {
			var oFields = xfa.layout.pageContent(nPage, "field");
			var nNodes = oFields.length;
			for (var n = 0; n < nNodes; n++) {
				oFields.item(n).execValidate();
			}
		}
	}

	// testZipCode
	// check if the value is a valid ZIP code in Germany
	function testZipCode(val)
	{
		var r = new RegExp("^[A-Z0-9\\-]+$", "i");

		var result = r.test(val);
		if (result == true) {
			return true;
		}
		else {
			return false;
		}
	}

	function checkEmail(val){

		var RegExpMail = new RegExp("^[a-zA-Z0-9_\\-\\.]+\\@[a-zA-Z0-9_\\-\\.]+\\.[a-zA-Z]{2,8}$");
		var CheckValue = val ;

		if(!CheckValue.match(RegExpMail)){
			app.alert("E-Mail address is not valid!");
			return false;
		}else{
			return true;
		}

	}

