	function isDate(s)
	{
		var r = new RegExp("^[0-9]{4}[-]{1}[0-9]{2}[-]{1}[0-9]{2}$", "i");

		if (r.test(s)) {
			if (s.substr(5, 2) <= 12 && s.substr(8, 2) <= 31) {
				return true;
			}
		}
		if (s == "" || s == null) {
			return true;
		}
		xfa.host.messageBox("invalid date format '" + s + "'");
		return false;
	}

	function isFutureDate(sDate)
	{
		var oNow = new Date();
		var lNow = oNow.getTime();
		sDate = sDate.replace(/-/g, "/");
		sDate = sDate.replace(/\./g, "/");
		var oDate = new Date(sDate);
		var lDate = oDate.getTime();
		if (lNow <= lDate) {
			return true;
		}
		else {
			return false;
		}
	}

	function isNumber(n) 
	{ 
		return !isNaN(parseFloat(n)) && !isNaN(n - 0); 
	}

