	var bFieldsHighlighted = true;

	// Applies or removes highlights from required (mandatory) fields.
	function HighlightRequiredFields(oNode, bApply)
	{
		var sHLColor = GetAcrobatHLColor();							// not really used
		sHLColor     = "255,255,0";									// yellow

		if (!bApply || sHLColor.length > 0) {
			return ProcessAllFields(oNode, bApply ? sHLColor : "255,255,255", !bApply); // when removing highlight color, use white because it's the XFA default
		}

		return 0;
	}

	// Returns the Acrobat Highlight Color in XFA's RGB format.
	function GetAcrobatHLColor()
	{
		var aAcroColor = app.runtimeHighlightColor; // returns an array: ["RGB",R,G,B] (RGB value) or ["G",G] (gray value)

		// In the color array, the red, green and blue components are represented by values between 0 (no color) and 1 (full color).
		// We want the color represented as values between 0 (no color) and 255 (full color).

		if (aAcroColor[0] == "RGB")
		{
			// RGB value -- straight-forward
			return Math.round(aAcroColor[1] * 255) + "," + Math.round(aAcroColor[2] * 255) + "," + Math.round(aAcroColor[3] * 255);
		}
		else if (aAcroColor[0] == "G")
		{
			// Gray value -- apply same intensity to all components
			return Math.round(aAcroColor[1] * 255) + "," + Math.round(aAcroColor[1] * 255) + "," + Math.round(aAcroColor[1] * 255);
		}
		else
		{
			// don't know how to translate color into RGB
			return "";
		}
	}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// Field Processor Functions (customized)
//
// These functions are called by the ProcessAllFields function
// depending on the kind of field being processed.
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

// Set the highlight color.
// oUITypeNode is the node which represents the field's type (button, checkButton, textEdit, etc.).
function SetColor(oUITypeNode, sColor)
{
	oUITypeNode.border.fill.color.value = sColor;
	oUITypeNode.border.edge.presence = "hidden";
}

// Reset the highlight color.
// oUITypeNode is the node which represents the field's type (button, checkButton, textEdit, etc.).
function ResetColor(oUITypeNode)
{
	oUITypeNode.border.fill.presence = "hidden";
	oUITypeNode.border.edge.presence = "hidden";
}

// Returns true if the field is mandatory and isn't filled.
// in: oField	a field object
function IsRequired(oField)
{
	if (oField.validate.nullTest == "error" && (oField.rawValue == null || oField.rawValue.length == 0)) {
		//var s = xfa.resolveNode(oField.name + ".assist.toolTip").value;
		var s = oField.assist.toolTip.value;
		if (oField.parent.className != "exclGroup") {
			glob.sMandatory = glob.sMandatory + "\n[" + oField.name + "]: " + s;
		}

		return true; // mandatory field
	}
	// If the field's parent is an exclusion group, the exclusion group's mandatory setting applies to all fields it contains.
	var oParent = oField.parent;
	if (oParent.className == "exclGroup" && oParent.validate.nullTest == "error" && (oParent.rawValue == null || oParent.rawValue.length == 0)) {
		//glob.sMandatory = glob.sMandatory + "\n[" + oParent.name + "] " + oParent.assist.toolTip.value;
		s = xfa.resolveNode(oParent.name + ".#field[" + oField.classIndex + "].assist.toolTip.value");
		if (s != "") {
			if (oField.classIndex == 0) {
				//glob.sMandatory = glob.sMandatory + "\n[" + oParent.name + " " + oField.name + "] " + s;
				glob.sMandatory = glob.sMandatory + "\n[" + oParent.name + " " + oField.name + "]: " + oParent.assist.toolTip.value;
			}
			else {
				//glob.sMandatory = glob.sMandatory + " " + s;
			}
		}
		return true; // parent is a mandatory exclusion group (e.g. radio button list)
	}

	if (oField.ui.oneOfChild.className == "checkButton" && oField.validate.nullTest == "error")
	{
		// Check boxes always have a default value (so it won't be null neither will it be an empty string).
		//  If a check box is mandatory, we'll assume that means it's value should either be "on" or "neutral" but not "off".
		// (Note that while check boxes can't be made mandatory via Designer's UI, they can be via script: See the Initialize script of the
		//  CheckBox3 field.)

		// By definition (in the XFA 2.2 spec), the "off" value is the second value in the first <items> child node.

		var oItemsList = oField.resolveNodes("#items"); // note the "#" prefix to access the <items> child nodes explicitely

		if (oItemsList.length > 0)
		{
			var oValueList = oItemsList.item(0).nodes;

			if (oValueList.length > 1)
			{
				var sOffValue = oValueList.item(1).value;

				if (oField.rawValue == sOffValue) {
					return true; // found a mandatory check box whose value is still set to "off"
				}
			}
		}
	}
	return false;
}

//
// Each of the following "original" Field Processor Functions returns true if the highlight color
// of the field was modified.
//

// oField is a check box object.
function ProcessCheckBox(oField, sColor, bRemove)
{
	if (bRemove || IsRequired(oField))
	{
		//SetColor(oField.ui.resolveNode("#checkButton"), sColor);
		SetColor(oField, sColor);
		return true;
	}
	ResetColor(oField);
	return false;
}

// oField is a radio button object (inside an exclusion (radio button) group).
function ProcessRadioButton(oField, sColor, bRemove)
{
	if (bRemove || IsRequired(oField))
	{
		// app.alert("ProcessRadioButton - required - " + oField.name);
		// SetColor(oField.ui.resolveNode("#checkButton"), sColor); // By default, in Designer, fields inside radio button lists are checkButton types.
		SetColor(oField, sColor);
		return true;
	}
	ResetColor(oField);
	return false;
}

// oField is a list box.
function ProcessListBox(oField, sColor, bRemove)
{
	if (bRemove || IsRequired(oField))
	{
		//SetColor(oField.ui.resolveNode("#choiceList"), sColor);
		SetColor(oField, sColor);
		return true;
	}
	ResetColor(oField);
	return false;
}

