/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// ProcessAllFields Function (customized)
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

// Process all fields found within the specified node (object).
// For each field that's found, the appropriate Field Processor Function
//  is called with a reference to the field itself.
// Returns the number of fields that were found which were required and not filled.
function ProcessAllFields(oNode, sColor, bRemove)
{
	var nNumRequired = 0;

	if (oNode.className == "exclGroup" || oNode.className == "subform"  || oNode.className == "subformSet" || oNode.className == "area") {
		// Look for child fields.

		for (var i = 0; i < oNode.nodes.length; i++) {
			var oChildNode = oNode.nodes.item(i);
			nNumRequired += ProcessAllFields(oChildNode, sColor, bRemove);
		}
	}
	else if (oNode.className == "field")
	{
		switch (oNode.ui.oneOfChild.className)
		{
			case "button":
				if (ProcessButton(oNode, sColor, bRemove)) {
					nNumRequired++;
				}
				break;
			case "checkButton":
				if (oNode.parent.className == "exclGroup") {
					// checkButtons inside exclusion groups are radio buttons
					if (ProcessRadioButton(oNode, sColor, bRemove)) {
						nNumRequired++;
					}
				}
				else {
					// otherwise, they're check boxes
					if (ProcessCheckBox(oNode, sColor, bRemove)) {
						nNumRequired++;
					}
				}
				break;
			case "choiceList":
			{
				var oUITypeNode = oNode.ui.resolveNode("#choiceList");

				if (oUITypeNode.open == "userControl") {
					// drop down list / combo box
					if (ProcessDropDownList(oNode, sColor, bRemove)) {
						nNumRequired++;
					}
				}
				else {
					// otherwise, it's a list box
					if (ProcessListBox(oNode, sColor, bRemove)) {
						nNumRequired++;
					}
				}
				break;
			}

			case "dateTimeEdit":
				if (ProcessDateTime(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			case "imageEdit":
				if (ProcessImageEdit(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			case "numericEdit":
				if (ProcessNumeric(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			case "passwordEdit":
				if (ProcessPassword(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			case "textEdit":
				if (ProcessTextEdit(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			case "signature":
				if (ProcessSignature(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			case "barcode":
				if (ProcessBarcodeEdit(oNode, sColor, bRemove))
				{
					nNumRequired++;
				}
				break;

			default:
				app.alert("unknown field type");
				break;
		}
	}

	return nNumRequired;
}

//+ GENERATED - DO NOT EDIT (ID:5CF89448-52E6-413A-878F-049F3F994A6C CRC:1706013869)
//+ Type: SetFocusOnFirstFailedField
function DoSetFocusOnFirstFailedField(oRootSubform) {

	if (xfa.host.name == "Acrobat") {

		var oInvalidNodes = oRootSubform.getInvalidObjects();
		var nCount = oInvalidNodes.length;

		if (nCount > 0) {

			xfa.host.setFocus(oInvalidNodes.item(0));
		}
	}
}
